DROP TABLE IF EXISTS `account`;CREATE TABLE `account` (  `id` INT(10) UNSIGNED ZEROFILL NOT NULL,  `value` VARCHAR(255) DEFAULT NULL,  `date_created` TIMESTAMP NULL DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=INNODB DEFAULT CHARSET=utf8;/*Data for the table `account` *//*Table structure for table `privilege` */DROP TABLE IF EXISTS `privilege`;CREATE TABLE `privilege` (  `id` INT(10) UNSIGNED ZEROFILL NOT NULL,  `value` VARCHAR(255) DEFAULT NULL,  `date_created` TIMESTAMP NULL DEFAULT NULL,  `description` VARCHAR(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=INNODB DEFAULT CHARSET=utf8;/*Data for the table `privilege` *//*Table structure for table `user` */DROP TABLE IF EXISTS `user`;CREATE TABLE `user` (  `id` INT(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `username` VARCHAR(255) DEFAULT NULL,  `password` VARCHAR(45) DEFAULT NULL,  `name` VARCHAR(255) DEFAULT NULL,  `date_added` TIMESTAMP NULL DEFAULT NULL,  `account_id` INT(10) UNSIGNED ZEROFILL NOT NULL,  `privilege_id` INT(10) UNSIGNED ZEROFILL NOT NULL,  PRIMARY KEY (`id`),  KEY `fk_user_account1` (`account_id`),  KEY `fk_user_privilege1` (`privilege_id`),  CONSTRAINT `fk_user_account1` FOREIGN KEY (`account_id`) REFERENCES `account` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,  CONSTRAINT `fk_user_privilege1` FOREIGN KEY (`privilege_id`) REFERENCES `privilege` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION) ENGINE=INNODB DEFAULT CHARSET=utf8;